import { useState, useRef, useEffect } from 'react';
import './../components/Home';
const uuid = require('uuid');

function Home() {
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [isCaptureEnabled, setIsCaptureEnabled] = useState(false);
  const [uploadResultMessage, setUploadResultMessage] = useState('Please capture an image to authenticate');
  const [visitorName, setVisitorName] = useState('placeholder.jpeg');
  const [isAuth, setAuth] = useState(false);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    // Initially, hide the capture button
    setIsCaptureEnabled(false);
  }, []);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      setIsCameraActive(true);
      setIsCaptureEnabled(true); // Enable capture button after stream is active
    } catch (err) {
      console.error('Error accessing camera: ', err);
    }
  };

  const captureImage = (e) => {
    e.preventDefault();
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

    canvas.toBlob((blob) => {
      const visitorImageName = uuid.v4();
      setVisitorName(`${visitorImageName}.jpeg`);
      sendImageToAWS(blob, visitorImageName);
    }, 'image/jpeg');
  };

  const sendImageToAWS = (imageBlob, visitorImageName) => {
    fetch(`https://48cc83boci.execute-api.us-east-1.amazonaws.com/dev/pjt-visitor-image-storage/${visitorImageName}.jpeg`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'image/jpeg',
      },
      body: imageBlob,
    })
      .then(async () => {
        const response = await authenticate(visitorImageName);
        if (response.Message === 'Success') {
          setAuth(true);
          setUploadResultMessage(`Hello, ${response['firstName']} ${response['lastName']}, WELCOME.`);
        } else {
          setAuth(false);
          setUploadResultMessage(`User NOT Found`);
        }
      })
      .catch((error) => {
        setAuth(false);
        setUploadResultMessage('There is an error in the authentication process, try again later.');
        console.error(error);
      });
  };

  // Authenticate the image with AWS
  async function authenticate(visitorImageName) {
    const requestUrl = `https://48cc83boci.execute-api.us-east-1.amazonaws.com/dev/employee?${new URLSearchParams({
      objectKey: `${visitorImageName}.jpeg`,
    })}`;
    return await fetch(requestUrl, {
      method: 'GET',
      headers: {
        Accept: 'application/json',
        'Content-Type': 'application/json',
      },
    })
      .then((response) => response.json())
      .then((data) => data)
      .catch((error) => console.error(error));
  }

  return (
    <div className="App">
      <div className="container">
        <h2 className="title">IMAGE AUTHENTICATION PORTAL</h2>

        {!isCameraActive ? (
          <button className="btn start-btn" onClick={startCamera}>
            Start Camera
          </button>
        ) : (
          <>
            <button className="btn capture-btn" type="submit" onClick={captureImage}>
              Capture & Authenticate
            </button>
            <div className="video-container">
              <video ref={videoRef} className="video-frame" autoPlay width={250} height={250} />
              <canvas ref={canvasRef} className="hidden-canvas" width={250} height={250} />
            </div>
          </>
        )}

        <div className={isAuth ? 'auth-message success' : 'auth-message failure'}>
          {uploadResultMessage}
        </div>
      </div>
    </div>
  );
}

export default Home;
